import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import LivroService from '../../services/LivroService.js';

const ListaLivro = () => {

    const [livro, setLivro] = useState([])

    useEffect(() => {

        getAllLivro();
    }, [])

    const getAllLivro = () => {
        LivroService.getAllLivro.then((response) => {
            setLivro(response.data)
            console.log(response.data);
        }).catch(error => {
            console.log(error);
        })
    }

    return (
        <div className="container">
            <h1 className="text-center"> Livros </h1>
            <Link to="/add-employee" className="btn btn-primary mb-2" > Adicionar Livro </Link>
            <table className="table table-bordered table-striped">
                <thead>
                    <th> Id </th>
                    <th> Titulo </th>
                    <th> Autor </th>
                    <th> Editora </th>
                    <th> Ano de Publicacao </th>
                    <th> Estoque </th>
                </thead>
                <tbody>
                    {
                        livro.map(
                            livro =>
                                <tr key={livro.id}>
                                    <td> {livro.titulo} </td>
                                    <td> {livro.autor} </td>
                                    <td>{livro.editora}</td>
                                    <td>{livro.anoPublicacao}</td>
                                    <td>{livro.estoque}</td>
                                    <td>
                                        <button>Delete</button>
                                    </td>
                                </tr>
                        )
                    }
                </tbody>
            </table>
        </div>
    )
}

export default ListaLivro;
