import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import ClienteService from "../../services/ClienteService"

const ListaClientes = () => {
    const [cliente, setCliente] = useState([]);

    useEffect(() => {
        getAllClientes();
    }, []);

    const getAllClientes = () => {
        ClienteService.getAllClientes()
            .then((response) => {
                setCliente(response.data);
                console.log(response.data);
            })
            .catch((error) => {
                console.log(error);
            });
    };

    const deleteCliente = (clienteId) => {
        ClienteService.deleteCliente(clienteId).then((response) => {
            getAllClientes();
        }).catch(error => {
            console.log(error);
        })
    }

    return (
        <div className="container-livro">
            <h1> Clientes </h1>
            <Link to="/add-cliente" className="btn mb-4 btn-lg">
                {" "}
                Novo Cliente{" "}
            </Link>
            <table className="table table-bordered table-striped">
                <thead>
                    <tr>
                        <th> <p>Id</p> </th>
                        <th> <p>Titulo</p> </th>
                        <th> <p>Autor</p> </th>
                        <th> <p>Editora</p> </th>
                        <th> <p>Ano Publicacao</p> </th>
                        <th> <p>Estoque</p> </th>
                        <th> <p>Ações</p> </th>
                    </tr>
                </thead>
                <tbody>
                    {cliente.map((cliente) => (
                        <tr key={cliente.id}>
                            <td> <p className="p_td">{cliente.id}</p></td>
                            <td> <p className="p_td">{cliente.titulo}</p> </td>
                            <td> <p className="p_td">{cliente.autor}</p> </td>
                            <td> <p className="p_td">{cliente.editora} </p> </td>
                            <td> <p className="p_td">{cliente.anoPublicacao} </p> </td>
                            <td> <p className="p_td">{cliente.estoque} </p> </td>
                            <td>
                                <div className="div-acoes">
                                    <Link className="btn" to={`/edit-cliente/${cliente.id}`} >Atualizar</Link>
                                    <button className="btn" onClick={() => deleteCliente(cliente.id)}
                                        >Deletar</button>
                                </div>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ListaClientes;
