import React, { useState, useEffect } from 'react'
import { Link, useParams } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import LivroService from '../../services/LivroService';

const Teste = () => {

    const [estoque, setEstoque] = useState('')
    const { id } = useParams();

    const adicionarProduto = (e) => {
        e.preventDefault();

        //const livro = { titulo, autor, editora, linkImg, anoPublicacao, estoque }
        const livro = { estoque }

        if (id) {
            LivroService.updateLivro(id, livro).then((response) => {
                window.location.href = "/";
            }).catch(error => {
                console.log(error)
            })

        } else {
            console.log("passe o id")
        }

    }

    useEffect(() => {

        LivroService.getLivroById(id).then((response) => {
            // setTitulo(response.data.titulo)
            //setAutor(response.data.autor)
            //setEditora(response.data.editora)
            //setLinkImg(response.data.linkImg)
            //setAnoPublicacao(response.data.anoPublicacao)
            setEstoque(response.data.estoque)
        }).catch(error => {
            console.log(error)
        })
    }, [])

    const title = () => {

        if (id) {
            return <h1 className="text-center">Atualizar Livro</h1>
        } else {
            return <h1 className="text-center">Adicionar Livro</h1>
        }
    }

    return (
        <div>
            <br /><br />
            <div className="container-add">
                <div className="row">
                    <div className="card col-md-6 offset-md-3 offset-md-3">
                        {
                            title()
                        }
                        <div className="card-body">
                            <form>                 
                               
                                <div className="form-group mb-2">
                                    <input
                                        type="estoque"
                                        placeholder="Estoque"
                                        name="estoque"
                                        className="form-control"
                                        value={estoque}
                                        onChange={(e) => setEstoque(e.target.value)}
                                    >
                                    </input>
                                </div>

                                <button className="btn" onClick={(e) => adicionarProduto(e)} >Salvar </button>
                                <Link to="/" className="btn"> Cancel </Link>
                            </form>

                        </div>
                    </div>
                </div>

            </div>

        </div>
    )
}


export default Teste;
