import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import VendaService from "../../services/VendaService"
import "./Vendas.css";

const ListaVendas = () => {
    const [vendas, setVendas] = useState([]);

    useEffect(() => {
        getAllVendas();
    }, []);

    const getAllVendas = () => {
        VendaService.getAllVendas
            .then((response) => {
                setVendas(response.data);
                console.log(response.data);
            })
            .catch((error) => {
                console.log(error);
            });
    };

    const deleteVenda = (vendaId) => {
        VendaService.deletevenda(vendaId).then((response) => {
            getAllVendas();
        }).catch(error => {
            console.log(error);
        })
    }

    return (
        <div className="container-livro">
            <h1> Vendas </h1>
            <Link to="/add-venda" className="btn mb-4 btn-lg">
                {" "}
                Nova Venda{" "}
            </Link>
            <table className="table table-bordered table-striped">
                <thead>
                    <tr>
                        <th> <p>Id</p> </th>
                        <th> <p>Cliente</p> </th>
                        <th> <p>Livro</p> </th>
                        <th> <p>Quantidade</p> </th>
                        <th> <p>Preço </p> </th>
                        <th> <p>Ações</p> </th>
                    </tr>
                </thead>
                <tbody>
                    {vendas.map((venda) => (
                        <tr key={venda.id}>
                            <td> <p className="p_td">{venda.id}</p></td>
                            <td> <p className="p_td">{venda.cliente}</p> </td>
                            <td> <p className="p_td">{venda.livro}</p> </td>
                            <td> <p className="p_td">{venda.qtdItens} </p> </td>
                            <td> <p className="p_td">{venda.precoVenda} </p> </td>
                            <td>
                                <div className="div-acoes">
                                    <Link className="btn" to={`/edit-venda/${venda.id}`} >Atualizar</Link>
                                    <button className="btn" onClick={() => deleteVenda(venda.id)}
                                        >Deletar</button>
                                </div>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ListaVendas;
